module CollectWorks
  def is_a_match row, field, field2=nil, row_name=nil
    row_name ||= field
    return true if row[row_name].to_s.contains_nothing?
    detection = \
    if field2
      $ascap_details.detect { |detail| detail[field][field2] == row[row_name] }
    else
      $ascap_details.detect { |detail| detail[field] == row[row_name] }
    end
  end

  def mismatch row, field
    raise "#{field} doesn't match \nrow: #{row}\n\n\ndetails:#{$ascap_details}"
  end

  def contains? row, key
    alternate_titles = row[key].split(',')
    return true if alternate_titles.empty?
    $ascap_details.detect do |detail|
      alternate_titles.detect do |alternate_title|
        detail[key].include? alternate_title
      end
    end
  end

  def get_ascap_details
    browser = WebBrowser.open url: Ascap::Login.url
    Ascap::Login browser: browser, username: @username, password: @password
    Ascap::WorksPage browser
    $ascap_details = []
    Ascap::WorksCollector browser do |details|
      $ascap_details << details
    end
    p $ascap_details
  end
  
  def to_bool string
    return string if string == true or string == false
    return !!string.to_s.match(/Y|yes|true/i)
  end
  
